xpath.script:
tailwind.config = {
    darkMode: "class",
    theme: {
        extend: {
            colors: {
                primary: '#2563eb',
                'primary-light': '#eff6ff',
                'primary-dark': '#1e40af',
                'background-light': '#f8fafc',
                'background-dark': '#020617',
                'text-light-primary': '#1e293b',
                'text-light-secondary': '#64748b',
                'text-dark-primary': '#f8fafc',
                'text-dark-secondary': '#94a3b8',
                'border-light': '#e2e8f0',
                'border-dark': '#1e293b',
            },
            fontFamily: {
                "display": ["Inter", "sans-serif"]
            },
            borderRadius: {
                "DEFAULT": "0.5rem",
                "lg": "0.75rem",
                "xl": "1rem",
                "full": "9999px"
            },
        },
    },
