body.script:

let currentBom = null;
let baseComponents = [];

function loadBomComponents() {
    const select = document.getElementById('bom_select');
    const bomId = select.value;

    if (!bomId) {
        document.getElementById('bomDetailsSection').style.display = 'none';
        currentBom = null;
        baseComponents = [];
        return;
    }

    // Get BOM data from select option
    const selectedOption = select.options[select.selectedIndex];
    const productName = selectedOption.getAttribute('data-product-name');
    const productQty = parseFloat(selectedOption.getAttribute('data-product-qty'));
    const productUom = selectedOption.getAttribute('data-product-uom');

    // Update product info
    document.getElementById('product_name_display').textContent = productName;
    document.getElementById('product_uom_display').textContent = productUom;
    document.getElementById('product_qty_input').value = productQty;
    document.getElementById('bom_id').value = bomId;

    // Fetch BOM components via AJAX
    fetch('/trcf_fnb_inventory/get_bom_components', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'X-CSRF-TOKEN': document.querySelector('meta[name="csrf-token"]').getAttribute('content')
        },
        body: JSON.stringify({
            jsonrpc: '2.0',
            method: 'call',
            params: {
                bom_id: bomId
            }
        })
    })
    .then(response => response.json())
    .then(data => {
        if (data.result && data.result.success) {
            currentBom = data.result.bom;
            baseComponents = data.result.components;
            renderComponents();
            document.getElementById('bomDetailsSection').style.display = 'block';
        } else {
            alert('Lỗi: ' + (data.result.error || 'Không thể tải thông tin BOM'));
        }
    })
    .catch(error => {
        console.error('Error:', error);
        alert('Lỗi khi tải thông tin BOM');
    });
}

function renderComponents() {
    const tbody = document.getElementById('componentsTableBody');
    tbody.innerHTML = '';

    const multiplier = parseFloat(document.getElementById('product_qty_input').value) / currentBom.product_qty;

    baseComponents.forEach(function(comp, index) {
        const adjustedQty = (comp.product_qty * multiplier).toFixed(2);

        const tr = document.createElement('tr');
        tr.className = 'hover:bg-slate-50 dark:hover:bg-slate-800/50 transition-colors';
        tr.innerHTML = 
            '<td class="px-6 py-4 text-text-light-primary dark:text-white font-medium text-sm leading-normal whitespace-nowrap">' + comp.product_name + '</td>' +
            '<td class="px-6 py-4 whitespace-nowrap">' +
                '<input onchange="updateComponentData()" class="component-qty form-input w-full rounded-lg text-text-light-primary dark:text-white focus:outline-none focus:ring-2 focus:ring-primary/50 border-border-light dark:border-border-dark bg-white dark:bg-slate-900 focus:border-primary dark:focus:border-primary text-sm px-3 py-2" type="number" min="0" step="0.01" value="' + adjustedQty + '" data-product-id="' + comp.product_id + '"/>' +
            '</td>' +
            '<td class="px-6 py-4 text-text-light-secondary dark:text-text-dark-secondary text-sm font-normal leading-normal whitespace-nowrap">' + comp.product_uom + '</td>';

        tbody.appendChild(tr);
    });
}

function updateComponentQuantities() {
    if (currentBom && baseComponents.length > 0) {
        renderComponents();
    }
}

function updateComponentData() {
    // This is called when user manually changes component quantity
    // We don't need to do anything here, just let the user edit
}

function submitForm() {
    // Validate
    const bomId = document.getElementById('bom_id').value;
    if (!bomId) {
        alert('Vui lòng chọn món cần chế biến');
        return;
    }

    const productQty = parseFloat(document.getElementById('product_qty_input').value);
    if (!productQty || productQty <= 0) {
        alert('Vui lòng nhập số lượng sản xuất hợp lệ');
        return;
    }

    const pickingTypeId = document.getElementById('picking_type_select').value;
    if (!pickingTypeId) {
        alert('Vui lòng chọn phiếu sản xuất');
        return;
    }

    // Collect component data
    const componentInputs = document.querySelectorAll('.component-qty');
    const componentsData = [];

    componentInputs.forEach(function(input) {
        const productId = input.getAttribute('data-product-id');
        const qty = parseFloat(input.value) || 0;

        componentsData.push({
            product_id: productId,
            qty: qty
        });
    });

    // Set hidden fields
    document.getElementById('product_qty_hidden').value = productQty;
    document.getElementById('components_data').value = JSON.stringify(componentsData);

    // Submit form
    document.getElementById('processingForm').submit();
}
